function googleSheetsToDate(excelNum) {
    const startDate = new Date(1899, 11, 30); // Fecha base de Google Sheets (30 de diciembre de 1899)
    startDate.setDate(startDate.getDate() + excelNum); // Ajustar el n√∫mero de d√≠as
    return startDate;
}

// Funci√≥n para formatear la fecha en MM/DD/YYYY
function formatDate(date) {
    const month = String(date.getMonth() + 1).padStart(2, '0'); // Mes (1-12)
    const day = String(date.getDate()).padStart(2, '0'); // D√≠a (1-31)
    const year = date.getFullYear(); // A√±o (yyyy)
    return `${month}/${day}/${year}`;
}

