const inputItem = $input.all()[0];

// 1. Acceder al dato binario 'data'
const rawBinaryData = inputItem.binary ? inputItem.binary.data : null; 

if (!rawBinaryData) {
    // Si no hay dato binario, salir.
    return [];
}

// 2. Comprobar si es un array. Si no lo es, envolverlo en un array.
// Esto soluciona el error "not iterable".
let allBinaryFiles;
if (Array.isArray(rawBinaryData)) {
    allBinaryFiles = rawBinaryData;
} else {
    // Es un solo archivo binario (un objeto), lo convertimos en un array de un elemento.
    allBinaryFiles = [rawBinaryData]; 
}

if (allBinaryFiles.length === 0) {
    return [];
}

let output = [];

// 3. Itera sobre el array de archivos binarios (que ahora est√° garantizado que es un array)
for (const file of allBinaryFiles) {
    let type;

    // Identifica el tipo de archivo por su MimeType
    if (file.mimeType === 'image/jpeg') {
        type = 'image';
    } else if (file.mimeType === 'video/mp4') {
        type = 'video';
    } else {
        // Ignorar otros tipos de archivo
        continue; 
    }

    // Crea un nuevo elemento de salida para este archivo
    output.push({
        // Incluye metadatos en el JSON
        json: {
            processType: type,
            fileName: file.fileName || 'file',
            mimeType: file.mimeType
        },
        // Adjunta el archivo binario usando la clave 'data'
        binary: {
            data: file 
        }
    });
}

return output;
